import { useState } from "react";

import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

function App() {
  const expenseItems = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  const [expenses, setExpenseItems] = useState(expenseItems);

  const saveExpenseHandler = (expenseData) => {
    const newExpenses = [...expenseItems, expenseData];
    setExpenseItems(newExpenses);
  };

  const handleFilterChange = (filterData) => {
    const newExpenses = expenses.filter(
      (e) => e.date.getFullYear() === filterData
    );
    console.log(expenses[0].date.getFullYear());
    setExpenseItems(newExpenses);
  };

  return (
    <div>
      <NewExpense onSaveExpense={saveExpenseHandler} />
      <Expenses expenses={expenses} onChangeFilter={handleFilterChange} />
    </div>
  );
}

export default App;
